  object-fit: cover;
  margin-left: 5rem;
  padding: 1rem;
}

.project-title {
  font-size: wrem;
  padding: 1rem 0.5rem;
}

.viewbtn {
  margin-top: -5%;
  padding: 2rem;
}

.btn {
  display: inline-block;
  padding: 1rem 2rem;
  border-radius: 2px;
}

.btn-show-all {
  font-size: 2rem;
  background: var(--main-gray);
  transition: background 0.3s ease-out;
}

.btn-show-all:hover {
  background: var(--main-red);
}

.btn-show-all:hover > i {
  transform: translateX(2px);
}

