/* Custom properties/variables  */
:root {
    --main-white: #f0f0f0;
    --main-gray: #303841;
    --main-red: #be3144;
    --main-cyan: #00FFFF;
  }
/* Base reset */
* {
    margin: 0;
    padding: 0;
  }

  /* box-sizing and font sizing */
  *,
  *::before,
  *::after {
    box-sizing: inherit;
  }

  html {
    box-sizing: border-box;

    /* Set font size for easy rem calculations
     * default document font size = 16px, 1rem = 16px, 100% = 16px
     * (100% / 16px) * 10 = 62.5%, 1rem = 10px, 62.5% = 10px
    */
    font-size: 62.5%;
    scroll-behavior: smooth;
  }
  body {
    font-family: 'Poppins', sans-serif;
    font-size: 1.8rem; /* 18px */
    font-weight: 400;
    line-height: 1.4;
    color: var(--main-white);
  }
  h1, h2 {
  font-family: 'Raleway', sans-serif;
  font-weight: 700;
  text-align: center;
  }

  ul {
    list-style: none;
  }

  a {
    text-decoration: none;
    color: var(--main-white);
  }
  img {
  display: block;
  width: 45%;
}

  /* A few media query to set some font sizes at different screen sizes.
   * This helps automate a bit of responsiveness.
   * The trick is to use the rem unit for size values, margin and padding.
   * Because rem is relative to the document font size
   * when we scale up or down the font size on the document
   * it will affect all properties using rem units for the values.
  */

  /* I am using the em unit for breakpoints
   * The calculation is the following
   * screen size divided by browser base font size
   * As an example: a breakpoint at 980px
   * 980px / 16px = 61.25em
  */

  /* 1200px / 16px = 75em */
  @media (max-width: 75em) {
    html {
      font-size: 60%;
    }
  }

  /* 980px / 16px = 61.25em */
  @media (max-width: 61.25em) {
    html {
      font-size: 58%;
    }
  }

  /* 460px / 16px = 28.75em */
  @media (max-width: 28.75em) {
    html {
      font-size: 55%;
    }
  }

/*Base styles*/
/*STARTS Navbar*/
#navbar{
    background-color:var( --main-gray)
}
.nav-list{
    height:10vh;
    display:flex;
    width:50%;;
    justify-content: space-around;
    align-items: center;
    margin-left:auto;
    list-style:none;
    background-color: var( --main-gray)
}
.nav-links{
    text-decoration: none;
    color:var( --main-white);
    font-size:18px;
}
/*Ends Navbar*/


/* STARTS Home Page*/
.home, .about, .work, .showwork, .contact{
    height:90vh;
    position:absolute;
    width:100%;
}
.home-content{
    height:100%;
    display:flex;
    justify-content: space-evenly;
    align-items: center;
    img{
        height:70%;
    }
    h1{
        font-family: 'Poppins', sans-serif;
        font-size:5.0rem;
        margin: 30px 0px;
        text-align: center;
        margin-right: 7rem;
    }
    p{
        font-size:25px;
        font-weight:200;
        font-style: italic;
        color:var(--main-gray);
        text-align: center;
        margin-right: 7rem;
    } 
}
.home{
    background-image: linear-gradient(to right, #ec77ab 0%, #7873f5 100%);
}
.icons{
  justify-content: center;
  margin-right: 5rem;
}
.icon{
    list-style: none;
    padding-left: 0px;
    display: flex;
    justify-content: center;
    width: 100%;
    max-width: 980px;
    margin-top: 4rem;
    flex-wrap: wrap;
    a{
        color:var(--main-white);
    }
}

/*ENDS Home Page*/

/*Start About PAge*/
.about-content{
    height:100%;
    display:flex;
    justify-content: space-evenly;
    align-items: center;
    img{
      height: 50%;
      width: 50%;
      margin-left: -25rem;
    }
    h1{
        font-size:25px;
        margin: 30px 0px;
        color:var(--main-white);
    }
    h3{
      font-size:18px;
      color:var(--main-white);
    }
    p{
        font-size:14px;
        color:var(--main-gray);
    }
    span{
      margin-left: 25rem;
      padding: 10px;
      color:var(--main-gray);
    }
}

.about{
    background-image: linear-gradient(to top, #fbc2eb 0%, #a6c1ee 100%);
}
.skills {
  height: 55rem;
    margin-left: -55rem;
    margin-top: -7rem;
    width: 89rem;
    padding: 3px 3px 3px 3px;
}
article {
  padding: 10px;
  display:grid;
  grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
}
.content{
  width: 65rem;
  margin-left: -10rem;
}

/*End About page*/
.work{
  background-image: linear-gradient(-225deg, #69EACB 0%, #EACCF8 48%, #6654F1 100%);

    h2{
      color:var(--main-gray);
    }
}
.footer-section{
  bottom:10rem;
}
footer{
  border-top: 4px solid var(--main-white);
}
/*End About page*/
.showwork{
  background-image: linear-gradient(-225deg, #FF3CAC 0%, #562B7C 52%, #2B86C5 100%);
}

/* Projects section */

.projects-section {
    text-align: center;
    padding: 1rem 2rem;
    background: var(--main-blue);

  }

  .projects-section-header {
    max-width: 640px;
    margin: 0 auto 6rem auto;
    border-bottom: 0.2rem solid var(--main-white);
  }

  @media (max-width: 28.75em) {
    .projects-section-header {
      font-size: 4rem;
    }
  }

  /* "Automagic" image grid using no media queries */
  .projects-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    grid-gap: 4rem;
    width: 100%;
    max-width: 1280px;
    margin: 0 auto;
    margin-bottom: 6rem;
  }
  .projects-grid-all {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    grid-gap: 4rem;
    width: 100%;
    max-width: 1280px;
    margin: 0 auto;
    margin-bottom: 6rem;
  }

  @media (max-width: 30.625em) {
    .projects-section {
      padding: 3rem 1rem;
    }

    .projects-grid {
      grid-template-columns: 1fr;
    }
  }

  .project {
    background: var(--main-gray);
    box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.5);
    border-radius: 2px;
  }

  .code {
    color: var(--main-gray);
    transition: color 0.3s ease-out;
  }

  .project:hover .code {
    color: #ff7f50;
  }

  .project-image {
    height: calc(100% - 6.8rem);
    width: 75%;
    object-fit: cover;
    margin-left: 5rem;
    padding: 1rem;
  }

  .project-title {
    font-size: wrem;
    padding: 1rem 0.5rem;
  }
  .viewbtn {
    margin-top: -5%;
    padding: 2rem;
}
  .btn {
    display: inline-block;
    padding: 1rem 2rem;
    border-radius: 2px;
  }

  .btn-show-all {
    font-size: 2rem;
    background: var(--main-gray);
    transition: background 0.3s ease-out;
  }

  .btn-show-all:hover {
    background: var(--main-red);
  }

  .btn-show-all:hover > i {
    transform: translateX(2px);
  }

  .btn-show-all > i {
    margin-left: 10px;
    transform: translateX(0);
    transition: transform 0.3s ease-out;
  }

/*  START Contact Page*/
.contact{
    background-image: linear-gradient(-225deg, #2CD8D5 0%, #C5C1FF 56%, #FFBAC3 100%);


}
.contact-section {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    text-align: center;
    width: 100%;
    height: 85vh;
    padding: 0 2rem;
  }

  .contact-section-header > h2 {
    font-size: 6rem;
  }

  @media (max-width: 28.75em) {
    .contact-section-header > h2 {
      font-size: 4rem;
    }
  }

  .contact-section-header > p {
    font-style: italic;
  }

  .contact-links {
    display: flex;
    justify-content: center;
    width: 100%;
    max-width: 980px;
    margin-top: 4rem;
    flex-wrap: wrap;
  }

  .contact-details {
    font-size: 2.4rem;
    text-shadow: 2px 2px 1px #1f1f1f;
    transition: transform 0.3s ease-out;
  }

  .contact-details:hover {
    transform: translateY(8px);
  }

/*End Contact Page*/

/* Footer */
.footer-section{
  margin-top: -4rem;
}
.footer-section2{
  margin-top: 3rem;
}
footer {
  font-weight: 300;
  display: flex;
  justify-content: space-evenly;
  padding: 2rem;
  background: var(--main-gray);
  border-top: 4px solid var(--main-cyan);
}

footer > p {
  margin: 2rem;
}

footer i {
  vertical-align: middle;
}

@media (max-width: 28.75em) {
  footer {
    flex-direction: column;
    text-align: center;
  }
}
